import logo from './smr.png';
import React from "react";
import {loadStripe} from '@stripe/stripe-js';
import './App.css';
import ReCAPTCHA from 'react-google-recaptcha';
// import ReCAPTCHA from "react-google-recaptcha";

var stripePromise = loadStripe('pk_test_51IWVXgLK0vLBTfXTBSMyEiRmQTzC14wWWTaKuJVoSvCeCeQtlHFWFSETdH0lebmaTg5UWAH01GZCqiWIYuWH4dzT00tjajonTm');

class App extends React.Component {
  constructor(props) {
    super(props)
    this.state = {
      results: "",
      total: 0,
      items: {}
    };
    this.updateTheTotal = this.updateTheTotal.bind(this);
    this.onChange = this.onChange.bind(this);
    this.handleClick = this.handleClick.bind(this);
    this.enableBtn = this.enableBtn.bind(this);
  }

  updateTheTotal() {
    this.setState({total: this.state.total + 50});
  }

  onChange(value) {
    console.log("Captcha value:", value);
  }

  enableBtn(){
    document.getElementById("button1").disabled = false;
  }

  handleClick = async () => {
    // Get Stripe.js instance
    const stripe = await stripePromise;

    stripe.redirectToCheckout({
      mode: "payment",
      lineItems: [{ price: "price_1IWsTeLK0vLBTfXTLTnSRPph", quantity: 1 }],
      // successUrl:
      //   'success.html?session_id={CHECKOUT_SESSION_ID}',
      // cancelUrl:
      //   'canceled.html?session_id={CHECKOUT_SESSION_ID}',
      successUrl:
        'https://shadowmiraclerecords.github.io/website/',
      cancelUrl:
        'https://shadowmiraclerecords.github.io/website/',
      shippingAddressCollection: {
        allowedCountries: ['US'],
      }
    }).then(this.handleStripeResult);
  };

  render() {
    const {
      handleClick,
      updateTheTotal
    } = this;
    // const cloud = {
      // font-size: "10px",
      // color: "#cccccc",
      // line-break: anywhere/
      // word-break: normal
      // overflow: "hidden"
      // white-space: nowrap
      // text-overflow: ellipsis
      // font-family: Interstate,Lucida Grande,Lucida Sans Unicode,Lucida Sans,Garuda,Verdana,Tahoma,sans-serif
      // font-weight: 100
    // };
    return (
      <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <form name="input" method="POST" action="https://formspree.io/f/xvovedjb">
          <div class="g-recaptcha" data-sitekey="your_site_key"></div>
          <input type="submit" value="Submit"></input>
        </form>
        <form name="input" method="POST" action="https://formspree.io/f/xvovedjb">
          Subscribe(: <input type="email" name="_replyto" placeholder="email">
          </input>
          <input type="submit" value="Send"></input>
        </form>
        {/* <ReCAPTCHA
            sitekey="6LeIxAcTAAAAAJcZVRqyHh71UMIEGNQ_MXjiZKhI"
          /> */}
        {/* <div class="g-recaptcha" data-sitekey="############-#####" data-callback="enableBtn"></div>
        <input type="button" value="Submit" id="button1" disabled="disabled"></input> */}
        {/* <div className="container">
          <img src={logo} alt="CDS" width="100" height="100"></img>
          <br></br>
          <button onClick={updateTheTotal}>
            Add This To The Cart
          </button>
          <br></br>
          <button>
            Go To Checkout ${this.state.total}
          </button>
        </div> */}
        {/* <script src="https://js.stripe.com/v3/"></script> */}
        <button role="link" onClick={handleClick}>
          Store
        </button>
        <iframe width="75%" height="127" scrolling="no" frameborder="no" allow="autoplay" src="https://w.soundcloud.com/player/?url=https%3A//api.soundcloud.com/tracks/1000565353&color=%23080808&auto_play=false&hide_related=true&show_comments=true&show_user=true&show_reposts=false&show_teaser=false&visual=false"></iframe>
      </header>
    </div>
    );
  }
}

export default App;
